import{_ as t,c as e,o as r,af as n}from"./chunks/framework.DJPGobOp.js";const m=JSON.parse('{"title":"其他新物品","description":"","frontmatter":{},"headers":[],"relativePath":"custom.md","filePath":"custom.md"}'),c={name:"custom.md"};function i(o,a,s,p,l,d){return r(),e("div",null,a[0]||(a[0]=[n('<h1 id="其他新物品" tabindex="-1">其他新物品 <a class="header-anchor" href="#其他新物品" aria-label="Permalink to “其他新物品”">​</a></h1><h2 id="传送石碑" tabindex="-1">传送石碑 <a class="header-anchor" href="#传送石碑" aria-label="Permalink to “传送石碑”">​</a></h2><h3 id="传送石碑-1" tabindex="-1">传送石碑 <a class="header-anchor" href="#传送石碑-1" aria-label="Permalink to “传送石碑”">​</a></h3><p>允许你进行长距离传送，图标和名称均可以在右键打开的 GUI 中配置</p><p>1Dot 在 <a href="https://modrinth.com/mod/sswaystones" target="_blank" rel="noreferrer">原模组</a> 的基础上加上了中文本地化、默认设为公开、坐标工具提示和维度信息显示</p><div class="note custom-block github-alert"><p class="custom-block-title">跨维度传送</p><p></p><p>跨维度传送时总是消耗 1 级经验</p><p>不同维度的传送石碑在 GUI 中图标右下角的堆叠数量不同，主世界为不显示，下界为 <code>2</code>，末地为 <code>3</code></p></div><p>合成配方如下：</p><p><img src="https://cdn.modrinth.com/data/cached_images/a8f6c01c75fb2b6a29669334740e01099a8adbed.png" alt=""></p><p>其中石砖墙 + 石砖可以被以下方块组合替换，也影响放置后的外观：</p><ul><li>深板岩砖 + 深板岩砖墙</li><li>凝灰岩砖 + 凝灰岩砖墙</li><li>泥砖 + 泥砖墙</li><li>树脂砖 + 树脂砖墙</li><li>下界砖 + 下界砖墙</li><li>红色下界砖 + 红色下界砖墙</li><li>末地石砖 + 末地石砖墙</li><li>砂岩 + 砂岩墙</li><li>红砂岩 + 红砂岩墙</li><li>海晶石 + 海晶石墙</li></ul><p>由于模组作者遗漏，磨制黑石砖墙、红砖墙、苔石砖墙虽然叫「砖墙」但是不能用</p><h3 id="便携传送石碑" tabindex="-1">便携传送石碑 <a class="header-anchor" href="#便携传送石碑" aria-label="Permalink to “便携传送石碑”">​</a></h3><p>随时随地在手上就可以打开传送菜单，合成配方如下：</p><p><img src="https://cdn.modrinth.com/data/cached_images/a46a47cd011454c9ad4d22dc3fb188bbdddf615c.png" alt=""></p><h2 id="国际象棋-polychess" tabindex="-1">国际象棋 PolyChess <a class="header-anchor" href="#国际象棋-polychess" aria-label="Permalink to “国际象棋 PolyChess”">​</a></h2><p>将黑色或白色混凝土放入切石机，即可获得国际象棋棋子</p><p><img src="https://cdn.modrinth.com/data/cached_images/f2a72e1dc7c6cf4e3d249906fbc84bccbde37da6.png" alt=""></p><h2 id="建筑手杖-construction-wand" tabindex="-1">建筑手杖 Construction Wand <a class="header-anchor" href="#建筑手杖-construction-wand" aria-label="Permalink to “建筑手杖 Construction Wand”">​</a></h2><p>请先参阅 <a href="https://modrinth.com/mod/construction-wand" target="_blank" rel="noreferrer">Modrinth 页面</a> 或 MC 百科，此部分稍后继续更新</p><p>使用建筑手杖，你可以一次放置许多个方块，从而扩展你面向一侧的建筑</p><p>你还可以升级手杖，添加额外的核心，让你能够在面向的方块后方放置一个方块，在空中召唤方块，或者快速摧毁大量方块</p><p>手杖分为由石头、铁和钻石制成的基础手杖和无限手杖，更高等级的手杖威力更大，使用寿命更长</p><p><img src="https://raw.githubusercontent.com/Theta-Dev/ConstructionWand/1.19/images/crafting1.png" alt=""></p><p><img src="https://raw.githubusercontent.com/Theta-Dev/ConstructionWand/1.19/images/crafting2.png" alt=""></p><p><img src="https://raw.githubusercontent.com/Theta-Dev/ConstructionWand/1.19/images/crafting3.png" alt=""></p><p><img src="https://raw.githubusercontent.com/Theta-Dev/ConstructionWand/1.19/images/crafting4.png" alt=""></p><p><img src="https://raw.githubusercontent.com/Theta-Dev/ConstructionWand/1.19/images/crafting5.png" alt=""></p><p><img src="https://raw.githubusercontent.com/Theta-Dev/ConstructionWand/1.19/images/crafting6.png" alt=""></p><h2 id="即将加入" tabindex="-1">即将加入 <a class="header-anchor" href="#即将加入" aria-label="Permalink to “即将加入”">​</a></h2><p>家具、工业模组、更多生物等</p>',30)]))}const g=t(c,[["render",i]]);export{m as __pageData,g as default};
